// Alla requires innan denna
const express = require("express");
const cors = require("cors");
const router = express.Router();


// Exempel körning av ett use för router ifall det behövs
router.use(cors(
   {
       credentials : true,
       origin : "http://127.0.0.1:5500",
   }
));


router.get("/test2", (req,res) => {
   res.send("<h1>Hello from /api/posts!</h1>")
})
// Alla andra app.get, app.post, osv byts ut mot
// router.get, router.post, router.put, ...


module.exports = router;